mapper.select:
SELECT id, ticket_name, person_kind, CAST(status AS CHAR) AS status, sell_price, order_time, stadium_name, order_number FROM order_base_info WHERE user_id = #{userId}
AND `status` = #{status}
AND order_number = #{orderNumber}
ORDER BY order_time DESC LIMIT #{start},#{pageSize}

mapper.update:
UPDATE order_content_detail oc
SET oc.`status` = #{status},
    oc.remarks=#{remarks}
WHERE
    oc.order_code = #{orderCode}

UPDATE order_content_detail oc
SET oc.`status` = #{status},
oc.remarks=#{remarks}
WHERE
oc.id = #{id}

UPDATE order_base_info ob
SET  ob.`status` = #{status},
	ob.remarks=#{remarks}
WHERE
ob.id = #{orderId}
